mapper.select:
SELECT a.*, b.NAME AS categoryName, group_concat( t.NAME SEPARATOR ',' ) AS tagNames FROM b_article a JOIN b_category b ON a.category_id = b.id JOIN b_article_tag atg ON a.id = atg.article_id JOIN b_tags t ON atg.tag_id = t.id
a.title like concat('%',#{param.title},'%')
and a.category_id=#{param.categoryId}
and a.is_publish=#{param.isPublish}
and atg.tag_id=#{param.tagId}
GROUP BY a.id order by a.is_stick desc ,a.create_time desc

select a.id,a.title,a.avatar,a.summary,a.quantity,a.content,a.content_md,a.is_secret,a.is_stick,a.is_publish,a.is_original,a.original_url,
       a.remark,a.create_time,a.keywords,b.name as categoryName from b_article a
                                                                         LEFT JOIN (SELECT id,name from b_category) b on a.category_id=b.id where a.id=#{id}

select id,title,avatar from b_article
where is_publish=#{publish}

    and category_id=#{categoryId}



      and id < #{id} order by id desc


        and id > #{id} order by id Asc


 limit 1

SELECT a.id AS id, a.title AS title, a.avatar AS avatar, a.content_md AS contentMd, a.is_stick AS isStick, a.create_time AS createTime, c.id AS categoryId, c.name AS categoryName FROM b_article a JOIN b_category c ON a.category_id = c.id WHERE a.is_publish = #{publish}
AND a.category_id = #{categoryId}
AND a.id IN ( SELECT article_id FROM b_article_tag WHERE tag_id = #{tagId})
ORDER BY a.is_stick DESC,
a.${orderByDescColumn} asc
a.create_time desc
